mapper.select:
select

from t_people


    region = #{region,jdbcType=BIGINT}


    community = #{community,jdbcType=BIGINT}


    and name like "%" #{name,jdbcType=VARCHAR} "%"


    and idcard like "%" #{idcard,jdbcType=VARCHAR} "%"


    and imsi in

      #{model}



    and phone in

      #{model}
